mapper.select:
SELECT 
FROM product

  WHERE product_name LIKE CONCAT('%', #{name}, '%')

ORDER BY ${sortColumn} ${sortOrder}
LIMIT #{limit} OFFSET #{offset}

mapper.update:
UPDATE product
shop_type = #{shopType}, product_name = #{productName}, original_price = #{originalPrice}, discount_price = #{discountPrice}, joined_count = #{joinedCount}, product_img = #{productImg}, quality_check = #{qualityCheck}, create_time = #{createTime}, update_time = #{updateTime},
WHERE id = #{id}
